['user_table', 6, 20, 4, None, '2007-03-02 14:49:06', 0, '<h2>Manage user</h2>\n\n<fieldset><legend>edit user</legend>\n{% if user_list %}\n  <table>\n  <tr>\n    <th>name</th>\n    <th>realname</th>\n    <th>email</th>\n    <th>admin</th>\n    <th>actions</th>\n  </tr>\n\n  {% for user in user_list %}\n  <tr>\n    <form method="post" action="{{ url }}">\n    <input name="id" type="hidden" value="{{ user.id }}" />\n    <td><input name="name" type="text" value="{{ user.name }}" size="15" maxlength="50" /></td>\n    <td><input name="realname" type="text" value="{{ user.realname }}" size="20" maxlength="50" /></td>\n    <td><input name="email" type="text" value="{{ user.email }}" size="30" maxlength="50" /></td>\n    <td><input type="checkbox" name="admin" value="1" {% if user.admin %}checked="checked"{% endif %}  title="checked if the user is an admin."/></td>\n    <td>\n      <input type="submit" value="new password" name="new password" />\n      <input type="submit" value="save" name="save" />\n      <input type="submit" value="delete" name="delete" onclick="return confirm(\'Really delete the User \\\'{{ user.name }}\\\'?\');"/>\n      <input type="submit" value="details" name="details" />\n    </td>\n    </form>\n  </tr>\n  {% endfor %}\n  </table>\n{% else %}\n  No users found!\n{% endif %}\n</fieldset>\n\n{% if user_details %}\n<fieldset><legend>user details</legend>\n<dl>\n  {% for line in user_details %}\n  <dt>{{ line.key }}</dt>\n  <dd>{{ line.value }}</dd>\n  {% endfor %}\n</dl>\n</fieldset>\n{% endif %}', '', '', 'Edit user table', '2007-03-02 14:49:06']
