['sub_menu', 11, 49, 2, 2, '2007-03-02 14:49:07', 0, 'h2. Administration Sub-Menu\n\n<fieldset><legend>page admin</legend>\n\n* "select page to edit":%(commandURLprefix)s/pageadmin/select_edit_page --(all pages available!)--\n* "select page to delete":%(commandURLprefix)s/pageadmin/select_del_page --(a select box, not delete the current page.)--\n* "sequencing the pages":%(commandURLprefix)s/pageadmin/sequencing --(change the page order)--\n\n</fieldset>\n\n<fieldset><legend>edit look</legend>\n\n* "edit stylesheets":%(commandURLprefix)s/edit_look/stylesheet\n* "edit templates":%(commandURLprefix)s/edit_look/template\n* "edit internal page":%(commandURLprefix)s/edit_look/internal_page\n\n</fieldset>\n\n<fieldset><legend>user management</legend>\n\n* "manage user":%(commandURLprefix)s/userhandling/manage_user\n\n</fieldset>\n\n<fieldset><legend>Module/Plugins management</legend>\n\n* "Module Administration":%(commandURLprefix)s/module_admin/menu\n* "Module Setup":%(commandURLprefix)s/module_admin/module_setup\n\n</fieldset>\n\n<fieldset><legend>backup</legend>\n\n* "DB dump":%(commandURLprefix)s/MySQLdump/menu\n\n</fieldset>\n\n<fieldset><legend>internals</legend>\n\n* <lucidTag:show_internals/>\n\n</fieldset>', '', '', 'Administration sub menu', '2007-03-02 14:49:07']
