['select_style', 15, 76, 4, None, '2007-03-02 14:49:07', 0, '{*\n    Jinja-Template\n*}\n<h2>Select CSS stylesheet to edit</h2>\n\n<fieldset class="select"><legend>edit/delete existing Stylesheets:</legend>\n<table>\n    <tr>\n        <th>name</th>\n        <th>description</th>\n        <th>action</th>\n    </tr>\n{% for item in itemsDict %}\n    {% cycle rowclass through "odd", "even" %}\n\n    <tr>\n        <form name="edit_{{ item.name }}" method="post" action="{{ url }}">\n            <input name="id" type="hidden" value="{{ item.id }}">\n            <td>\n               {% if item.name equals default_name %}\n                   <strong>{{ item.name|escape }}</strong> <small>(default)</small>\n               {% else %}\n                   {{ item.name|escape }}\n               {% endif %}\n            </td>\n            <td class="description">{{ item.description }}</td>\n            <td><input type="submit" value="edit" name="edit" /> <input type="submit" value="del" name="del" /></td>\n        </form>\n    </tr>\n{% endfor %}\n</table>\n</fieldset>\n\n<fieldset class="make_new_css"><legend>make a new Stylesheet:</legend>\n<p>\n  If you wont to make a new stylesheet, you should clone a existing one and edit the cloned css.\n</p>\n<form name="edit_style" method="post" action="{{ url }}">\n    Duplicate <select name="clone_name">\n    {% for item in nameList %}\n        <option value="{{ item }}">{{ item }}</option>\n    {% endfor %}\n\n    </select> to a new named: <input name="new_name" value="" size="20" maxlength="50" type="text">\n    <input type="submit" name="clone" value="clone" />\n</form>\n</fieldset>\n\n<fieldset class="set_default_css"><legend>mark a Stylesheet to the default one:</legend>\n<p>\n  If you make a new page, the stylesheet who marked as the default style would be used.\n</p>\n<form name="edit_style" method="post" action="{{ url }}">\n    Set <select name="default">\n    {% for item in itemsDict %}\n        <option value="{{ item.name }}" {% if item.name equals default_name %}selected="selected"{% endif %}>\n           {{ item.name }}\n        </option>\n    {% endfor %}\n    </select> to the default stylesheet.\n\n    <input type="submit" name="set" value="set" />\n</form>\n</fieldset>', '', '.select table td {\n  padding: 0.2em;\n}', 'select a stylesheet to edit it', '2007-03-02 14:49:07']
['edit_internal_page', 15, 77, 4, None, '2007-03-02 14:49:07', 0, '<form method="post" action="{{ url }}">\n  You edit the internal page: &quot;<strong>{{ name }}</strong>&quot;:\n\n    <p id="action_buttons">\n      <input type="submit" value="apply" name="apply" />\n      <input type="submit" value="save" name="save" />\n      <input type="reset" onClick="self.location.href=\'{{ url }}\'" value="abort" />\n      <span class="separator">|||</span>\n      <a href="javascript:show_html();" id="html_toggle">html</a>\n      <a href="javascript:show_css();" id="css_toggle">css</a>\n      <a href="javascript:show_js();" id="js_toggle">js</a>\n      <span class="separator">|||</span>\n      <a href="JavaScript:resize_big();" title="click to make the editor window bigger">&nbsp;+&nbsp;</a>\n      <a href="JavaScript:resize_small();" title="click to make the editor window smaller">&nbsp;-&nbsp;</a>\n      <span class="separator">|||</span>\n      <a href="{{ list_url }}" onclick="OpenInWindow(this.href); return false" title="Liste available PyLucid tags and functions">tags/functions</a>\n    </p>\n\n    <textarea wrap="off" class="ipage_edit" rows="25" accept-charset="UTF-8" id="ipage_html" name="content_html" >{{ content_html }}</textarea>\n    <textarea wrap="off" class="ipage_edit" rows="25" accept-charset="UTF-8" id="ipage_css" name="content_css">{{ content_css }}</textarea>\n    <textarea wrap="off" class="ipage_edit" rows="25" accept-charset="UTF-8" id="ipage_js" name="content_js">{{ content_js }}</textarea>\n\n    <input name="internal_page_name" type="hidden" value="{{ name }}" />\n\n    <br />\n    <fieldset id="ipage_details">\n    <legend>details</legend>\n       <label class="left" for="description">Description:</label>\n       <input id="description" name="description" type="text" style="width: 70%;" value="{{ description }}" />\n       <br />\n       <label class="left" for="template_engine">Template engine:</label>\n       <select class="right" id="template_engine" name="template_engine">\n          {% for item in engines %}\n             <option value="{{ item.id }}" {% if item.id equals current_engine %}selected="selected"{% endif %}>{{ item.name }}</option>\n          {% endfor %}\n       </select>\n       <br />\n       <label class="left" for="markup">Markup:</label>\n       <select class="right" id="markup" name="markup">\n          {% for item in markups %}\n             <option value="{{ item.id }}" {% if item.id equals current_markup %}selected="selected"{% endif %}>{{ item.name }}</option>\n          {% endfor %}\n       </select>\n    </fieldset>\n</form>\n\n<h4>Node:</h4>\n<ul>\n  <li>You may not delete internal jinja tags, like: \'{{ name }}\'</li>\n  <li>Normal percent sign must be quoted: \'%\' -&gt; \'%%\'</li>\n  <li>Markup doesn\'t work with every page, don\'t change it.</li>\n</ul>\n\n<script type="text/javascript">show_html();</script>', 'function show_html() {\n    hover(\'html_toggle\');\n    show("ipage_details", focus=true);\n    show(\'ipage_html\', focus=false);\n}\nfunction show_css() {\n    hover(\'css_toggle\');\n    show(\'ipage_css\', focus=true);\n    hide("ipage_details");\n}\nfunction show_js() {\n    hover(\'js_toggle\');\n    show(\'ipage_js\', focus=true);\n    hide("ipage_details");\n}\nfunction show(obj_name, focus) {\n    obj = document.getElementById(obj_name);\n    obj.style.display = "";\n    if (focus) obj.focus();\n    if (obj_name != \'ipage_html\') {hide(\'ipage_html\')};\n    if (obj_name != \'ipage_css\') {hide(\'ipage_css\')};\n    if (obj_name != \'ipage_js\') {hide(\'ipage_js\')};\n}\nfunction hide(obj_name) {\n    obj = document.getElementById(obj_name);\n    obj.style.display = "none";\n}\nfunction hover(obj_name) {\n    obj = document.getElementById(obj_name);\n    obj.style.borderColor = "#555 #ddd #ddd #555";\n    obj.style.backgroundColor = "#fff";\n    if (obj_name != \'html_toggle\') {normal(\'html_toggle\')};\n    if (obj_name != \'css_toggle\') {normal(\'css_toggle\')};\n    if (obj_name != \'js_toggle\') {normal(\'js_toggle\')};\n}\nfunction normal(obj_name) {\n    obj = document.getElementById(obj_name);\n    obj.style.borderColor = "";\n    obj.style.backgroundColor = "";\n}\n\nfunction resize_big()   {\n    bigger("ipage_html");\n    bigger("ipage_css");\n    bigger("ipage_js");\n}\nfunction bigger(obj_name)   {\n    textarea = document.getElementById(obj_name);\n    textarea.rows = textarea.rows*1.5;\n}\nfunction resize_small() {\n    smaller("ipage_html");\n    smaller("ipage_css");\n    smaller("ipage_js");\n}\nfunction smaller(obj_name) {\n    textarea = document.getElementById(obj_name);\n    textarea.rows = textarea.rows/1.5;\n}\n\n// tinyTextile help and TagList in a window\nfunction OpenInWindow(URL) {\n  win1 = window.open(URL, "", "width=1000, height=600, dependent=yes, resizable=yes, scrollbars=yes, location=no, menubar=no, status=no, toolbar=no");\n  win1.focus();\n}', 'label, input, select, textarea {\n  vertical-align: middle;\n  margin-top: 5px;\n  margin-bottom: 5px;\n}\nlabel.left {\n  float: left;\n  text-align: right;\n  width: 10em;\n  margin-right: .3em;\n  padding: 0px;\n}\n.rightonly {\n  /* Eingabefelder mit Text dahinter */\n  margin-left: 10.3em;\n  padding: 0px;\n}\n\nform br {\n  /*  floats nach dem Formular aufheben */\n  clear: both;\n}\n\nlabel, select, input {\n  cursor: pointer;\n}\ntextarea.ipage_edit {\n  width: 100%;\n}\n\n.internalpage input[type=text] {\n  width: 75%;\n  cursor: text;\n}\n\n.internalpage input, .internalpage select, textarea.ipage_edit {\n  margin: 2px;\n  border: 1px solid grey;\n  color: #000;\n  background: #fafafa;\n}\n.internalpage input:focus, .internalpage select:focus, textarea.ipage_edit:focus  {\n  border: 1px solid black;\n  background: #fff;\n}\n\n#action_buttons {\n    padding: 0 1em 0 1em;\n    background-color: #09C;\n}\n#action_buttons a {\n    text-decoration:none;\n    margin: 0px;\n    padding: 0.1em 2em 0.1em 2em;\n    font-family:Verdana;\n    line-height:2em;\n    font-size:0.75em;\n}\n#action_buttons a:link {    /* noch nicht besuchter Link */\n    color: black;\n    background-color: #f5f5f5;\n    border: 1px solid;\n    border-color: #ddd #555 #555 #ddd;\n}\n#action_buttons a:hover {   /* Maus \xc3\xbcber Link */\n    color: white;\n    background-color: #aaa;\n    border-color: #555 #ddd #ddd #555;\n}\n#action_buttons .separator {\n    padding: 0 1em 0 1em;\n}', 'edit a internal page', '2007-03-02 14:49:07']
